def list_cameras():
   for camera in cameras["CameraDetails"]["Camera"]:
    obj = {"geo": {"lat": camera["Location"]["Latitude"], "lon": camera["Location"]["Longitude"]}}
    if "Source" in camera:
        obj["description"] = camera["Description"]
        obj["id"] = camera["CameraId"]
    else:
        obj["description"] = camera["TourName"] 
        obj["id"] = camera["CameraTourId"]
    obj["format"] = [f"http://live.cotrip.org:1935/liveStreams/{i['StreamName']}" for i in camera["CameraView"]]
    yield obj

